import React from 'react';
import { Row, Col, Card } from 'react-bootstrap';
import BotaoEngrenagem from './BotaoEngrenagem';

const CardDemanda = ({ titulo, demandante, responsavel, descricao, cor, validade, onOpenModal }) => {
    // Função para formatar a data no formato desejado (DD/MM/YYYY)
    const formatarData = (data) => {
        const date = new Date(data);
        if (!isNaN(date.getTime())) {
            return `${date.getDate()}/${date.getMonth() + 1}/${date.getFullYear()}`;
        }
        return 'Data inválida';
    };

    return (
        <Card className="card-demanda">
            <Row className="g-0" style={{ height: '100%' }}>
                <Col xs={1} className="cor-col" style={{ backgroundColor: cor }}></Col>
                <Col xs={11} className="p-3" style={{ display: 'flex', flexDirection: 'column', height: '100%', margin: '-4%' }}>
                    <Card.Body style={{ flex: 1, position: 'relative' }}>
                        <Row className="align-items-center" style={{ marginTop: '0' }}>
                            <Col xs={11} className="text-center">
                                <Card.Title>{titulo}</Card.Title>
                            </Col>
                            <Col xs={1} className="text-end botao-engrenagem">
                                <BotaoEngrenagem onClick={onOpenModal} />
                            </Col>
                        </Row>
                        <Row>
                            <Col>
                                <p><strong>Demandante:</strong> {demandante?.nome || 'Não informado'}</p>
                            </Col>
                        </Row>
                        <Row>
                            <Col>
                                <p><strong>Responsável:</strong> {Array.isArray(responsavel) && responsavel.length > 0 ? responsavel.map(r => r.nome).join(', ') : 'Nenhum responsável'}</p>
                            </Col>
                        </Row>
                        <Row>
                            <Col>
                                <p><strong>Descrição:</strong> {descricao || 'Sem descrição'}</p>
                            </Col>
                        </Row>
                        <Row>
                            <Col>
                                <p><strong>Data de Validade:</strong> {validade ? formatarData(validade) : 'Não definida'}</p>
                            </Col>
                        </Row>
                    </Card.Body>
                </Col>
            </Row>
        </Card>
    );
};

export default CardDemanda;
