import React, { useState, useEffect } from 'react'; 
import { Form, Row, Col, ToggleButton, ToggleButtonGroup } from 'react-bootstrap';
import '../css/NovaDemanda.css';
import Botao from './Botao';
import criarDemanda from '../utils/home/criarDemanda';

// Funções para buscar os demandantes, responsáveis e status na API
const fetchDemandantes = async () => {
    const response = await fetch('http://localhost:8000/api/demandantes/'); // Substitua pela URL correta
    const data = await response.json();
    return data;
};

const fetchResponsaveis = async () => {
    const response = await fetch('http://localhost:8000/api/responsaveis/'); // Substitua pela URL correta
    const data = await response.json();
    return data;
};

const fetchStatus = async () => {
    const response = await fetch('http://localhost:8000/api/status/'); // URL para listar os status
    const data = await response.json();
    return data;
};

const NovaDemanda = () => {
    const [status, setStatus] = useState('Aguardando');
    const [nome, setNome] = useState('');
    const [demandante, setDemandante] = useState('');
    const [responsaveis, setResponsaveis] = useState([]); // Agora um array para múltiplos responsáveis
    const [descricao, setDescricao] = useState('');
    const [validade, setValidade] = useState('');
    const [demandantes, setDemandantes] = useState([]);
    const [todosResponsaveis, setTodosResponsaveis] = useState([]); // Responsáveis da API
    const [statuses, setStatuses] = useState([]);

    useEffect(() => {
        // Buscar demandantes, responsáveis e status na API quando o componente for montado
        const fetchData = async () => {
            const demandantesData = await fetchDemandantes();
            const responsaveisData = await fetchResponsaveis();
            const statusesData = await fetchStatus();  // Agora usa a URL de status configurada
            setDemandantes(demandantesData);
            setTodosResponsaveis(responsaveisData); // Responsáveis completos
            setStatuses(statusesData); // Configura os statuses dinamicamente
        };

        fetchData();
    }, []);

    const handleResponsavelChange = (e) => {
        // Atualiza os responsáveis selecionados
        const selectedResponsaveis = Array.from(e.target.selectedOptions, option => option.value);
        setResponsaveis(selectedResponsaveis);
    };

    const handleSalvar = async () => {
        const novaDemanda = {
            descricao: nome,
            demandante,
            responsaveis, // Agora passa o array de responsáveis
            validade,
            status,
        };

        const sucesso = await criarDemanda(novaDemanda);
        if (sucesso) {
            window.location.reload(); // recarrega a página
        }
    };

    return (
        <div className="menu-suspenso p-3">
            {/* Nome */}
            <Form.Group as={Row} className="mb-3">
                <Form.Label column sm={3} className="obrigatorio">Nome*</Form.Label>
                <Col sm={9}>
                    <Form.Control type="text" required placeholder="Digite o nome"
                        value={nome} onChange={(e) => setNome(e.target.value)} />
                </Col>
            </Form.Group>

            {/* Status */}
            <Form.Group as={Row} className="mb-3">
                <Form.Label column sm={3}>Status:</Form.Label>
                <Col sm={9}>
                    <ToggleButtonGroup type="radio" name="status" value={status} onChange={setStatus}>
                        {statuses.length > 0 ? (
                            statuses.map((item) => (
                                <ToggleButton
                                    key={item.id}
                                    id={item.id}
                                    value={item.nome}
                                    style={{
                                        backgroundColor: status === item.nome ? item.cor : '#6c757d', // Usando o código hexadecimal da cor
                                        color: 'white',
                                    }}
                                >
                                    {item.nome}
                                </ToggleButton>
                            ))
                        ) : (
                            <div>Carregando status...</div>
                        )}
                    </ToggleButtonGroup>
                </Col>
            </Form.Group>

            {/* Demandante */}
            <Form.Group as={Row} className="mb-3">
                <Form.Label column sm={3} className="obrigatorio">Demandante*</Form.Label>
                <Col sm={9}>
                    <Form.Control as="select" required value={demandante} onChange={(e) => setDemandante(e.target.value)}>
                        <option value="">Selecione um demandante</option>
                        {demandantes.map((item) => (
                            <option key={item.id} value={item.id}>{item.nome}</option>
                        ))}
                    </Form.Control>
                </Col>
            </Form.Group>

            {/* Responsáveis (Agora múltiplos) */}
            <Form.Group as={Row} className="mb-3">
                <Form.Label column sm={3} className="obrigatorio">Responsáveis*</Form.Label>
                <Col sm={9}>
                    <Form.Control as="select" multiple required value={responsaveis} onChange={handleResponsavelChange}>
                        {todosResponsaveis.map((item) => (
                            <option key={item.id} value={item.id}>{item.nome}</option>
                        ))}
                    </Form.Control>
                </Col>
            </Form.Group>

            {/* Descrição */}
            <Form.Group as={Row} className="mb-3">
                <Form.Label column sm={3}>Descrição:</Form.Label>
                <Col sm={9}>
                    <Form.Control as="textarea" rows={3} placeholder="Digite a descrição"
                        value={descricao} onChange={(e) => setDescricao(e.target.value)} />
                </Col>
            </Form.Group>

            {/* Data de vencimento */}
            <Form.Group as={Row} className="mb-3">
                <Form.Label column sm={3}>Data de vencimento:</Form.Label>
                <Col sm={9}>
                    <Form.Control type="date" required
                        value={validade} onChange={(e) => setValidade(e.target.value)} />
                </Col>
            </Form.Group>

            {/* Botão de envio */}
            <div className="text-end">
                <Botao texto="Salvar" onClick={handleSalvar} tipo="btn-salva" />
            </div>
        </div>
    );
};

export default NovaDemanda;
