import axios from 'axios';

export const EditarAtividade = async (atividadeId, nome, status, demandante, responsavel, descricao, validade, dataVencimento) => {
    if (!atividadeId) {
        console.log("ID da atividade não encontrado.");
        return;
    }

    try {
        // Preparar o objeto de dados conforme esperado pelo serializer
        const dadosAtualizados = {
            descricao,
            validade, // Adicionando o campo validade
            demandante, 
            status, 
            responsaveis: responsavel,  // Pode ser um array com IDs dos responsáveis
            data_vencimento: dataVencimento
        };

        // Enviar os dados para o endpoint de edição
        const response = await axios.put(`http://localhost:8000/api/atividades/${atividadeId}/editar/`, dadosAtualizados, {
            headers: {
                "Content-Type": "application/json",
            }
        });

        console.log("Atividade editada com sucesso:", response.data);
    } catch (error) {
        console.error("Erro ao editar atividade:", error);
    }
};
