import axios from 'axios';

// Função para deletar a atividade
export const DeletarAtividade = async (atividadeId, setAtividadeId) => {
    if (!atividadeId) {
        console.log("ID da atividade não encontrado.");
        return;
    }

    try {
        // Requisição DELETE para a URL correta
        const response = await axios.delete(`http://localhost:8000/api/atividades/${atividadeId}/deletar/`);

        // Verifica se a resposta da API foi bem-sucedida
        if (response.status === 204) { // Código de sucesso para DELETE
            console.log("Atividade deletada com sucesso:", response.data);
            // Limpar o ID após a exclusão
            setAtividadeId(null);
        } else {
            console.error("Erro inesperado ao deletar atividade:", response.data);
        }
    } catch (error) {
        console.error("Erro ao deletar atividade:", error);
    }
};

export default DeletarAtividade
